$('#comment-form').validate_popover({
    events: 'submit',
    popoverPosition: 'top',
    onkeyup: false,
    selector: 'input[type!=submit], select, textarea',
    callback: function(elem, valid) {
        if (!valid) {
            $(elem).addClass('error');
            return false;
        }
    },
    submitHandler: function(form) {
        var url = $(form).attr('action');
        $.post(url, $(form).serialize()).done(function(data) {
            $('.alert-area').html('<div class="alert alert-' + data.status + '" role="alert">' +
                '<button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">&times;</span><span class="sr-only">Kapat</span></button>' + data.message + '</div>');
            if (data.status == 'success') {
                $('#comment-form input[name="parent_id"]').val('');
                $(form).find("textarea").val("");
            }
        });
        return false;
    }
});
$('#comments .reply').click(function(e) {
    var id = $(this).parents('.comment').data('id');
    var message = $(this).parents('.comment').data('reply-message');
    $('#comment-form input[name="parent_id"]').val(id);
    $('.alert-area').html('<div class="alert alert-info" role="alert">' +
        '<button type="button" class="close btn btn-primary btn-xs" data-dismiss="alert">İptal</button>' + message + '</div>');
    $('html, body').stop(true).animate({
        scrollTop: $('#comments').offset().top - 100
    }, 'slow');
    e.preventDefault();
    return false;
});
$('#comment-form').on('click', '.btn.close', function() {
    $('#comment-form input[name="parent_id"]').val('');
});
